{
  // Simulated earthquake data along plate boundaries
  const quakes = [];
  // Ring of Fire
  for (let i = 0; i < 60; i++) {
    const angle = (i / 60) * Math.PI * 1.6 - Math.PI * 0.8;
    quakes.push({
      lon: -165 + 85 * Math.cos(angle) + (Math.random() - 0.5) * 6,
      lat: 8 + 38 * Math.sin(angle) + (Math.random() - 0.5) * 4,
