    earthQuakeData = feed.features.map(f =&gt; ({
      lon: f.geometry.coordinates[0],
      lat: f.geometry.coordinates[1],
      mag: f.properties.mag,
      place: f.properties.place,
      time: new Date(f.properties.time).toLocaleDateString(),
