  svg.append("circle")
    .attr("cx", cx)
    .attr("cy", cy)
    .attr("r", 20)
    .attr("fill", "#f1c40f");

  svg.append("text")
    .attr("x", cx)
    .attr("y", cy + 5)
    .attr("text-anchor", "middle")
    .attr("font-size", "12px")
    .text("â˜€ï¸");

  // Draw Earth positions
  svg.append("circle")
    .attr("cx", cx + c + a)
    .attr("cy", cy)
    .attr("r", 10)
    .attr("fill", "#3498db");

  svg.append("text")
    .attr("x", cx + c + a)
    .attr("y", cy - 20)
    .attr("text-anchor", "middle")
    .attr("font-size", "10px")
    .text("Aphelion (farthest)");

  svg.append("circle")
    .attr("cx", cx + c - a)
    .attr("cy", cy)
    .attr("r", 10)
    .attr("fill", "#2ecc71");

  svg.append("text")
    .attr("x", cx + c - a)
    .attr("y", cy - 20)
    .attr("text-anchor", "middle")
    .attr("font-size", "10px")
    .text("Perihelion (closest)");

  // Info box
  svg.append("rect")
    .attr("x", 10)
    .attr("y", 10)
    .attr("width", 200)
    .attr("height", 80)
    .attr("fill", "#f5f5f5")
    .attr("rx", 5);

  svg.append("text")
    .attr("x", 20)
    .attr("y", 35)
    .attr("font-size", "12px")
    .text(`Eccentricity: ${eccentricity.toFixed(3)}`);

  const distDiff = ((2 * a * eccentricity) / a * 100).toFixed(1);
  svg.append("text")
    .attr("x", 20)
    .attr("y", 55)
    .attr("font-size", "12px")
    .text(`Distance variation: ${distDiff}%`);

  svg.append("text")
    .attr("x", 20)
    .attr("y", 75)
    .attr("font-size", "11px")
    .attr("fill", "#666")
    .text(`Cycle: ~100,000 years`);

  return svg.node();
}









