  // Chart area
  const chartLeft = 80;
  const chartRight = 700;
  const chartTop = 50;
  const chartBottom = 290;
  const chartWidth = chartRight - chartLeft;
  const chartHeight = chartBottom - chartTop;

  // Scales
  const xScale = (yr) =&gt; chartLeft + ((yr - 2025) / 55) * chartWidth;
  const yScale = (pct) =&gt; chartBottom - ((pct - 50) / 50) * chartHeight;

  // Tipping point zone
  svg.append("rect").attr("x", chartLeft).attr("y", yScale(80)).attr("width", chartWidth)
    .attr("height", yScale(75) - yScale(80)).attr("fill", "#ff767533");
